import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { fetchProgramByName, updateProgram } from '../services/api';

const ProgramEdit = () => {
  const { name } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    location: '',
    startDate: '',
    endDate: '',
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadProgram();
    // eslint-disable-next-line
  }, [id]);

  const loadProgram = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await fetchProgramById(id);
      const p = response.data.program;
      setFormData({
        name: p.name || '',
        description: p.description || '',
        location: p.location || '',
        startDate: p.startDate ? p.startDate.slice(0, 10) : '',  // YYYY-MM-DD
        endDate: p.endDate ? p.endDate.slice(0, 10) : '',
      });
    } catch (err) {
      setError(err?.response?.data?.error || 'Failed to load program');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      await updateProgram(id, formData);
      setSuccess('Program updated successfully!');
      setTimeout(() => {
        setSuccess('');
        navigate(`/programs/${id}`);
      }, 1200);
    } catch (err) {
      setError(err?.response?.data?.error || 'Failed to update program');
    }
  };

  if (loading) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h2>Loading Program...</h2>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ padding: '20px' }}>
        <div style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '12px 20px',
          borderRadius: '6px',
          marginBottom: '20px',
          border: '1px solid #f5c6cb'
        }}>
          {error}
        </div>
        <button
          onClick={() => navigate('/programs')}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          ← Back to Programs
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: 600, margin: '0 auto' }}>
      {success && (
        <div style={{
          background: '#d4edda',
          color: '#155724',
          padding: '12px 20px',
          borderRadius: '6px',
          marginBottom: '20px',
          border: '1px solid #c3e6cb'
        }}>
          ✅ {success}
        </div>
      )}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 32 }}>
        <h1 style={{ margin: 0 }}>Edit Program</h1>
        <button
          onClick={() => navigate(`/programs/${id}`)}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          ← Cancel
        </button>
      </div>

      <form onSubmit={handleSubmit}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
          <div>
            <label style={{ fontWeight: '500' }}>
              Program Name *
              <input
                type="text"
                name="name"
                value={formData.name}
                required
                onChange={handleInputChange}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  marginTop: 6
                }}
              />
            </label>
          </div>
          <div>
            <label style={{ fontWeight: '500' }}>
              Description
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                rows={4}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  marginTop: 6,
                  fontFamily: 'inherit'
                }}
              />
            </label>
          </div>
          <div>
            <label style={{ fontWeight: '500' }}>
              Location
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  marginTop: 6
                }}
              />
            </label>
          </div>
          <div style={{ display: 'flex', gap: '16px' }}>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: '500' }}>
                Start Date
                <input
                  type="date"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleInputChange}
                  style={{
                    width: '100%',
                    padding: '8px 12px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    marginTop: 6
                  }}
                />
              </label>
            </div>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: '500' }}>
                End Date
                <input
                  type="date"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleInputChange}
                  style={{
                    width: '100%',
                    padding: '8px 12px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    marginTop: 6
                  }}
                />
              </label>
            </div>
          </div>
        </div>
        <button
          type="submit"
          style={{
            background: '#28a745',
            color: 'white',
            border: 'none',
            padding: '10px 24px',
            borderRadius: '5px',
            cursor: 'pointer',
            marginTop: 24,
            fontSize: 16
          }}
        >
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default ProgramEdit;
