import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import {
  fetchProgramByName,
  fetchParticipants,
  addParticipant,
  removeParticipant,
} from '../services/api';

const ProgramsDetail = () => {
  const { name } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [program, setProgram] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [allParticipants, setAllParticipants] = useState([]);
  const [addParticipantId, setAddParticipantId] = useState('');

  useEffect(() => {
    loadProgram();
    loadAllParticipants();
    // eslint-disable-next-line
  }, [name]);

  const loadProgram = async () => {
    try {
      setLoading(true);
      setError('');
      const resp = await fetchProgramByName(name);
      setProgram(resp.data.program);
    } catch (err) {
      setError(err?.response?.data?.error || 'Failed to load program');
      setProgram(null);
    } finally {
      setLoading(false);
    }
  };

  const loadAllParticipants = async () => {
    try {
      const resp = await fetchParticipants();
      setAllParticipants(resp.data?.participants || resp.data || []);
    } catch (err) {
      // not fatal, ignore
    }
  };

  const handleAddParticipant = async (e) => {
    e.preventDefault();
    if (!addParticipantId) return;
    try {
      await addParticipant(name, { participantId: addParticipantId });
      setSuccess('Participant added!');
      setAddParticipantId('');
      await loadProgram();
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError(err?.response?.data?.error || 'Failed to add participant');
    }
  };

  const handleRemoveParticipant = async (participantId) => {
    if (!window.confirm('Are you sure you want to remove this participant from the program?')) return;
    try {
      await removeParticipant(name, participantId);
      setSuccess('Participant removed!');
      await loadProgram();
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError(err?.response?.data?.error || 'Failed to remove participant');
    }
  };

  if (loading) {
    return (
      <div style={{ padding: 20, textAlign: 'center' }}>
        <h2>Loading Program...</h2>
      </div>
    );
  }

  if (error || !program) {
    return (
      <div style={{ padding: 20 }}>
        <div style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '12px 20px',
          borderRadius: 6,
          marginBottom: 20,
          border: '1px solid #f5c6cb'
        }}>
          {error || 'Program not found'}
        </div>
        <button
          onClick={() => navigate('/programs')}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: 5,
            cursor: 'pointer'
          }}
        >
          ← Back to Programs
        </button>
      </div>
    );
  }

  const participants = program.participants || [];

  return (
    <div style={{ padding: 20 }}>
      {success && (
        <div style={{
          background: '#d4edda',
          color: '#155724',
          padding: '12px 20px',
          borderRadius: 6,
          marginBottom: 20,
          border: '1px solid #c3e6cb'
        }}>
          ✅ {success}
        </div>
      )}
      {error && (
        <div style={{
          background: '#f8d7da',
          color: '#721c24',
          padding: '12px 20px',
          borderRadius: 6,
          marginBottom: 20,
          border: '1px solid #f5c6cb'
        }}>
          ❌ {error}
        </div>
      )}

      <div style={{ marginBottom: 20, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <h1 style={{ margin: '0 0 5px 0' }}>{program.name}</h1>
          <button
            onClick={() => navigate('/programs')}
            style={{
              background: 'transparent',
              color: '#667eea',
              border: 'none',
              padding: '5px 0',
              cursor: 'pointer',
              fontSize: 14,
              textDecoration: 'underline'
            }}
          >
            ← Back to Programs
          </button>
        </div>
        {user?.role === 'heartSmiles' && (
          <button
            onClick={() => navigate(`/programs/${encodeURIComponent(program.name)}/edit`)}
            style={{
              background: '#ffc107',
              color: '#333',
              border: 'none',
              padding: '10px 20px',
              borderRadius: 5,
              cursor: 'pointer',
              fontWeight: 500
            }}
          >
            Edit Program
          </button>
        )}
      </div>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: 20,
        marginBottom: 30
      }}>
        {/* Basic Information */}
        <div style={{
          background: 'white',
          border: '1px solid #e1e5e9',
          borderRadius: 8,
          padding: 20
        }}>
          <h3 style={{
            margin: '0 0 15px 0',
            color: '#333',
            borderBottom: '2px solid #667eea',
            paddingBottom: 10
          }}>
            Basic Information
          </h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
            <div>
              <strong style={{ color: '#666', fontSize: 13 }}>Name:</strong>
              <div style={{ marginTop: 3, color: '#333' }}>{program.name}</div>
            </div>
            {program.description && (
              <div>
                <strong style={{ color: '#666', fontSize: 13 }}>Description:</strong>
                <div style={{ marginTop: 3, color: '#333' }}>{program.description}</div>
              </div>
            )}
            {program.location && (
              <div>
                <strong style={{ color: '#666', fontSize: 13 }}>Location:</strong>
                <div style={{ marginTop: 3, color: '#333' }}>{program.location}</div>
              </div>
            )}
            {program.startDate && (
              <div>
                <strong style={{ color: '#666', fontSize: 13 }}>Start Date:</strong>
                <div style={{ marginTop: 3, color: '#333' }}>{new Date(program.startDate).toLocaleDateString()}</div>
              </div>
            )}
            {program.endDate && (
              <div>
                <strong style={{ color: '#666', fontSize: 13 }}>End Date:</strong>
                <div style={{ marginTop: 3, color: '#333' }}>{new Date(program.endDate).toLocaleDateString()}</div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Participants */}
      <div style={{
        background: 'white',
        border: '1px solid #e1e5e9',
        borderRadius: 8,
        padding: 20,
        marginBottom: 30
      }}>
        <h3 style={{
          margin: '0 0 15px 0',
          color: '#333',
          borderBottom: '2px solid #667eea',
          paddingBottom: 10
        }}>
          Participants ({participants.length})
        </h3>
        {participants.length > 0 ? (
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: 10 }}>
            {participants.map((participant, idx) => (
              <div
                key={participant.id || idx}
                style={{
                  background: '#f8f9fa',
                  padding: '10px 15px',
                  borderRadius: 6,
                  border: '1px solid #e1e5e9',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 12,
                  position: 'relative'
                }}
              >
                <span style={{ fontWeight: 500, color: '#333' }}>
                  {participant.name}
                </span>
                {user?.role === 'heartSmiles' && (
                  <button
                    onClick={() => handleRemoveParticipant(participant.id)}
                    style={{
                      marginLeft: 'auto',
                      background: '#dc3545',
                      color: 'white',
                      border: 'none',
                      padding: '4px 10px',
                      borderRadius: 4,
                      cursor: 'pointer',
                      fontSize: 12
                    }}
                  >
                    Remove
                  </button>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div style={{ color: '#666', fontStyle: 'italic', textAlign: 'center', padding: 20 }}>
            No participants yet.
          </div>
        )}

        {user?.role === 'heartSmiles' && (
          <form onSubmit={handleAddParticipant} style={{ marginTop: 20, display: 'flex', alignItems: 'center', gap: 10 }}>
            <select
              value={addParticipantId}
              onChange={e => setAddParticipantId(e.target.value)}
              style={{ minWidth: 200, padding: '6px 10px' }}
            >
              <option value="">Add participant...</option>
              {allParticipants
                .filter(ap => !participants.some(p => p.id === ap.id))
                .map(ap => (
                  <option key={ap.id} value={ap.id}>{ap.name}</option>
                ))}
            </select>
            <button
              type="submit"
              disabled={!addParticipantId}
              style={{
                background: '#28a745',
                color: 'white',
                border: 'none',
                padding: '8px 16px',
                borderRadius: 5,
                cursor: addParticipantId ? 'pointer' : 'not-allowed'
              }}
            >
              Add
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default ProgramsDetail;
