import { useEffect, useState } from 'react';
import { fetchParticipants } from '../services/api';

export default function Participants() {
  const [participants, setParticipants] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchParticipants().then(({ data }) => setParticipants(data.participants || [])).catch(() => setError('Failed to load'));
  }, []);

  return (
    <div>
      <h2>Participants</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <ul>
        {participants.map((p) => (
          <li key={p.id}>{p.name} {p.identificationNumber ? `(${p.identificationNumber})` : ''}</li>
        ))}
      </ul>
    </div>
  );
}
